// src/components/Chatbot.js
import React, { useState } from "react";
//import { lexRuntime } from "../aws-config";

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [inputText, setInputText] = useState("");
  const [messages, setMessages] = useState([]);

  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  const handleSend = async () => {
    const params = {
      botId: "your-bot-id",
      botAliasId: "your-bot-alias",
      localeId: "your-locale-id", // e.g., 'en_US'
      sessionId: "session-id",
      text: inputText,
      requestAttributes: {},
      sessionState: {},
    };

    try {
      const response = await lexRuntime.recognizeText(params).promise();
      setMessages([
        ...messages,
        { from: "user", text: inputText },
        { from: "bot", text: response.messages[0].content },
      ]);
      setInputText("");
    } catch (err) {
      console.error("Error sending message to bot:", err);
    }
  };

  return (
    <div style={{ position: "fixed", bottom: 10, right: 10 }}>
      <button onClick={() => setIsOpen(!isOpen)}>Chat</button>
      {isOpen && (
        <div
          style={{
            width: 300,
            height: 400,
            border: "1px solid black",
            padding: 10,
            overflow: "auto",
          }}
        >
          <div>
            {messages.map((msg, idx) => (
              <div
                key={idx}
                style={{ textAlign: msg.from === "user" ? "right" : "left" }}
              >
                {msg.text}
              </div>
            ))}
          </div>
          <input type="text" value={inputText} onChange={handleInputChange} />
          <button onClick={handleSend}>Send</button>
        </div>
      )}
    </div>
  );
};

export default Chatbot;
