import React, { useState } from "react";
import { Container, Typography, Box, TextField, Button } from "@mui/material";
import { useNavigate } from "react-router-dom";
import logo from "../dal_logo.png"; // Update this path to your actual logo file
import backgroundImage from "../background.jpg"; // Update this path to your actual background image file

const SecurityQuestionPage = () => {
  const [answer, setAnswer] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Assuming you already have the email or other necessary details stored
    const userEmail = "user@example.com"; // Replace this with the actual user email

    // API URL - replace 'your-api-gateway-url' with your actual API Gateway endpoint
    const apiUrl = "https://your-api-gateway-url/verifySecurityAnswer";

    try {
      const response = await fetch(apiUrl, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: userEmail,
          securityAnswer: answer,
        }),
      });

      const responseData = await response.json();
      if (response.ok && responseData.status === "success") {
        console.log("Verification Success:", responseData);
        navigate("/dashboard"); // Navigate only on successful verification
      } else {
        console.error("Verification Failed:", responseData.message);
        alert("Verification failed: " + responseData.message); // Display error message
      }
    } catch (error) {
      console.error("Network Error:", error);
      alert("Network error, please try again later.");
    }
  };

  return (
    <Container
      component="main"
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        minHeight: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        padding: 0,
      }}
    >
      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          padding: 4,
          boxShadow: 3,
          borderRadius: 2,
          bgcolor: "background.paper",
          width: "100%",
          maxWidth: "400px",
        }}
      >
        <img
          src={logo}
          alt="Logo"
          style={{ width: "100px", marginBottom: "20px" }}
        />
        <Typography component="h1" variant="h5" gutterBottom>
          Security Question
        </Typography>
        <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1 }}>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="security-answer"
            label="Answer the security question"
            name="answer"
            autoFocus
            value={answer}
            onChange={(e) => setAnswer(e.target.value)}
            sx={{ bgcolor: "background.default" }}
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            sx={{ mt: 3, mb: 2, bgcolor: "primary.main" }}
          >
            Submit
          </Button>
        </Box>
      </Box>
    </Container>
  );
};

export default SecurityQuestionPage;
